let data = [
    {
        search: 'all'
    }
]
let datos = []

const searchAllProducts = () => {
    fetch('http://localhost:3000/api/searchAllProducts', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        }
    })
        .then(result => result.json())
        .then(response => {
            datos = response
        })
    if (datos = []) {
        setTimeout(() => {
            return datos
        }, 1000);
    } else {
        return datos
    }

};


export default searchAllProducts
