//traer informacion de state

export const articles = (state) => {
    const result = (state.productsDescription)
    const slicedArray = result.slice(0, 2);
    return slicedArray
}


export const getProductById = (state) => (id = '') => {
    const entry = state.productsDescription.find(entry => entry.id === id)
    if (entry === undefined) {
        console.log('POR QUE?')
        const try2 = state.productsDescription.find(entry => entry.id === '1')
        console.log('TRY2', try2)
    }
    console.log('getProductById GETTER', entry)
    if (!entry) return
    return console.log('LLORA')
}
