// export const myGetter = (state) => {
// return state,

// }


//traer informacion de state

const slice = [0, 20]

export const articles = (state) => {
    const result = (state.productsDescription)
    const slicedArray = result.slice(slice);
    return slicedArray
}



export const getProductById = (state) => (id = '') => {
    const entry = state.productsDescription.find(entry => entry.id === id)
    if (!entry) return
    return { ...entry }
}

export const getProductByTerm = (state) => (term = '') => {
    if (term.length === 0) return state.productsDescription
    return state.productsDescription.filter(entry => entry.title.toLowerCase().includes(term.toLowerCase()))
}
