import axios from "axios";

// const getTodoItems = () => {
const getTodoItems = async () => {

    const BASE_URL = 'https://fakestoreapi.com/products/';


    let data = []

    const itemsAPI = async () => {
        console.log('llamo')
        try {
            const response = await axios.get(`${BASE_URL}`);
            data = response.data;
        } catch (errors) {
            console.error(errors);
        }
    }
    itemsAPI()

    console.log(data)

    setTimeout(() => {
        console.log(data)
    }, 4000)


    if (data = []) {
        console.log('vacio')

    } else {
        fetch('http://localhost:3000/api/datosAPI', {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }

        })

            .then(result => {
                console.log('buenas', JSON.stringify
                    (result))
                return result.json()
            })

            .then(response => {
                console.log("soy responde", response)
                if (response.error) {
                    var element = document.getElementById("empty_Container3");
                    element.style.display = "none";
                    Swal.fire({
                        title: 'Error',
                        icon: "error",
                        text: "Usuario o contraseña incorrecto",
                        timer: 2000,
                        timerProgressBar: false,
                        showConfirmButton: false,
                        allowEscapeKey: false,
                        allowOutsideClick: false,
                        background: '#fff',
                    }).then(() => {
                        element.style.display = "block";
                    })
                } else {
                    var element = document.getElementById("empty_Container3");
                    element.style.display = "none";
                    Swal.fire({
                        title: 'Success',
                        icon: "success",
                        text: "Bienvenido!",
                        timer: 2000,
                        timerProgressBar: false,
                        showConfirmButton: false,
                        allowEscapeKey: false,
                        allowOutsideClick: false,
                        background: '#fff',
                    })
                    setTimeout(() => {
                        top.window.open('https://ilercon.co/dashboard2', '_self').close(
                            // top.window.open('http://localhost:58766/', '_self').close(
                            element.style.display = "block"
                        )
                    }, 2001)
                    //  top.switchModal('hide')
                }
            })

    }

};




export default getTodoItems
