//traer informacion de state

export const articles = (state) => {
    const result = (state.productsDescription.find(({ id }) => id === 1))
    return result
}


export const getEntryById = (state) => (id = '') => {
    const entry = state.entries.find(entry => entry.id === id)
    if (!entry) return
    return { ...entry }
}
